/**
 * SEMANTIC DESIGN TOKENS
 * 
 * These tokens map primitive values to semantic purposes.
 * This abstraction allows for easy theme changes and ensures consistency.
 */
:root {
 /* ===== TEXT COLORS ===== */
 --text-main: var(--clr-gray-100); /* Primary body text */
